import { useState } from "react";

export const UserInput = () => {
  const [userInput, setUserInput] = useState({
    enteredUserName: "",
    enteredAge: 0,
  });

  const userNameUpdateHandler = (event) => {
    setUserInput();
  };

  return (
    <form className="user-input">
      <div className="user-input__name">
        <label>Username</label>
        <input value={userInput.enteredUserName}></input>
      </div>
      <div className="user-input__age">
        <label>Age</label>
        <input min="0" max="99" value={userInput.enteredUserAge}></input>
      </div>
      <button type="submit">Add User</button>
    </form>
  );
};
