import { useState } from "react";

export const UserInput = () => {
  const [userInput, setUserInput] = useState({
    enteredUserName: "",
    enteredAge: 0,
  });

  const userNameUpdateHandler = (event) => {
    setUserInput((prevState) => {
      return { ...prevState, enteredUserName: event.target.value };
    });
  };

  const userAgeUpdateHandler = (event) => {
    setUserInput((prevState) => {
      return { ...prevState, enteredAge: event.target.value };
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const newUser = {
      enteredUserName: userInput.enteredUserName,
      enteredAge: userInput.enteredAge,
    };

    setUserInput({
      enteredUserName: "",
      enteredAge: 0,
    });
  };

  return (
    <form className="user-input" onSubmit={submitHandler}>
      <div className="user-input__name">
        <label>Username</label>
        <input
          type="text"
          value={userInput.enteredUserName}
          onChange={userNameUpdateHandler}
        ></input>
      </div>
      <div className="user-input__age">
        <label>Age</label>
        <input
          type="number"
          value={userInput.enteredUserAge}
          onChange={userAgeUpdateHandler}
        ></input>
      </div>
      <button type="submit">Add User</button>
    </form>
  );
};
