import { useState } from "react";

export const UserInput = () => {
  const [userInput, setUserInput] = useState({
    enteredUserName: "",
    enteredAge: 0,
  });

  const userNameUpdateHandler = (event) => {
    setUserInput((prevState) => {
      return { ...prevState, enteredUserName: event.taget.value };
    });
  };

  const userAgeUpdateHandler = (event) => {
    setUserInput((prevState) => {
      return { ...prevState, enteredAge: event.taget.value };
    });
  };

  return (
    <form className="user-input">
      <div className="user-input__name">
        <label>Username</label>
        <input
          type="text"
          value={userInput.enteredUserName}
          onChange={userNameUpdateHandler}
        ></input>
      </div>
      <div className="user-input__age">
        <label>Age</label>
        <input
          type="number"
          min="0"
          max="99"
          value={userInput.enteredUserAge}
          onChange={userAgeUpdateHandler}
        ></input>
      </div>
      <button type="submit">Add User</button>
    </form>
  );
};
