import { Card } from "./Card";
import { Button } from "./Button";

import classes from "./ErrorModal.module.css";

export const ErrorModal = (props) => {
  return (
    <Card>
      <div className={classes.backdrop} />
      <header>
        <h2>{props.title}</h2>
      </header>
      <div>
        <p>{props.message}</p>
      </div>
      <footer>
        <Button>Ok</Button>
      </footer>
    </Card>
  );
};
